import { createApp } from 'vue';
import App from './App.vue';
import router from './router';
import { setAuthHeader } from './services/auth';

const app = createApp(App);

// 페이지 로드 시, 로컬스토리지에 있으면 헤더에 설정
setAuthHeader();

app.use(router);
app.mount('#app');
